import React, { useState } from 'react';
import instruct_gif from "../src/convexHullComponents/convex_instruct.gif";

function MyComponent() {
    const [modalOpen, setModalOpen] = useState(false);

    const showModal = () => {
        setModalOpen(true);
    };

    const hideModal = () => {
        setModalOpen(false);
    };
        return (
            <div>
                <button className="btn btn-primary btn-lg m-2" onClick={showModal}>Description</button>

                {modalOpen && (
                    <div className="modal display-block">
                          <div class="modal-main1 intro">
                            <div class="modal-header">
                                  <h5 class="modal-title">Pathfinding</h5>
                                  <button type="button" class="close" data-dismiss="modal">&times;</button>
                              </div>
                            <div class="modal-body intro">
                              <p>
                                  Pathfinding is generally the process of finding a route between two points. It is closely related to the shortest path problem in graph theory,
                                  which examines how to identify the "best" paths valued by different criteria (Ex. distance, cost, time consumption).
                              </p>
                              <p>Now please choose a sorting algorithm and visualize it!</p>
                                <p class='tips'>(after choosing an algorithm, click on the [Actions] button.)</p><br />
                                <p class='tips'>Note: there could be multiple "best" paths, so paths generated by different algorithms may not be consistent.</p>
                            </div>
                            <div class="modal-footer">
                              <button className="btn btn-outline-success my-2 my-sm-0" type="submit" onClick={hideModal}>Close</button>
                            </div>
                          </div>
                  </div>
                    
                )}

                <style jsx>
                    {`
                        .modal {
                            position: fixed;
                            top: 0;
                            left: 0;
                            width:100%;
                            height: 100%;
                            background: rgba(0, 0, 0, 0.6);
                            z-index: 1000;
                            border-radius: 5px;
                          }
                          
                          .modal-main1 {
                            position:fixed;
                            padding: 10px;
                            background: white;
                            color: rgb(32, 31, 31);
                            width: 38%;
                            height: auto;
                            top:50%;
                            left:50%;
                            transform: translate(-50%,-50%);
                            z-index: 1000;
                            border-radius: 5px;
                            font-size: 20px;
                          }
                          
                          .display-block {
                              display: block;
                          }
                          
                          .display-none {
                            display: none;
                          }
                        .modalImage{
                          position: fixed;
                          z-index: 1;
                          padding-top: 100px;
                          left: 0;
                          top: 0;
                          width: 100%;
                          height: 100%;
                          overflow: auto;
                          background-color: rgba(0, 0, 0, 0.6);
                        }
                        .intro{
                          text-align: left;
                          font-size: 17px;
                          line-height: 1.6;
                      }
                      .tips{
                        color: rgb(99, 99, 99);
                        margin-top: -15px;
                        font-size: 16px;
                    }
                    `}
                </style>
            </div>
        );
}

export default MyComponent;
